@Component({
  selector: 'app-nh-icon',
  templateUrl: './nh-icon.component.html',
  styleUrls: ['./nh-icon.component.scss']
})
export class NhIconComponent implements OnInit, AfterViewInit {


  @ViewChild('container', {static: true}) iconName;
  @Input() width;
  @Input() height;

  constructor() {
  }

  ngAfterViewInit(): void {
    if (icons[this.iconName.nativeElement.innerHTML]) {
      this.iconName.nativeElement.innerHTML = this.prepareIcon(icons[this.iconName.nativeElement.innerHTML]);
    } else {
      this.iconName.nativeElement.innerHTML = '';
      console.error('There is no such icon')
    }
  }

  prepareIcon(icon) {
    if (this.width) {
      icon = icon.replace(/width="(.+?")/g, `width="${this.width}`);
    }
    if (this.height) {
      icon = icon.replace(/height="(.+?")/g, `height="${this.height}`);
    }
    return icon;
  }


  ngOnInit(): void {
  }

}

